import 'react-number-picker/dist/style.css'

// import NumberPicker from 'react-number-picker'
import React from 'react'

const $Container = styled.div`
	display: flex;
`

function NumberPicker({ currentUPC, setCurrentUPC }) {
	function handleChange(e) {
		setCurrentUPC(e.target.value)
	}

	return (
		<$Container>
			{currentUPC.split('').map((digit, i) => (
				<input value={digit} type='number' onChange={handleChange} />
			))}
		</$Container>
	)
}

export default NumberPicker
