const Textarea = {
	width: '300px',
	height: '400px',
}

export const PasteArea = ({ setText }) => {
	async function paste(input) {
		const text = await navigator.clipboard.readText()
		input.value = text
		setText(text)
	}

	return <textarea style={Textarea} onClick={paste}></textarea>
}
