const Textarea = {
	width: '300px',
	height: '400px',
}

export const PasteArea = ({ setText }) => {
	async function paste(clickEvent) {
		console.log(clickEvent)
		let text = await navigator.clipboard.read()
		console.log(text)
		if (typeof text === 'string') setText(text)
	}

	return <button onClick={paste}>Click to paste your clipboard</button>
}
