import './App.css'

import Barcode from 'react-barcode'
import logo from './logo.svg'
import { text } from './text-sample'

const re1 = /[\d]{0,3}[\w]{0,2}[\d]{7,}/g

function Base64DecodeUrl(str) {
	str = str ? (str + '===').slice(0, str.length + (str.length % 4)) : ''
	return str.replace(/-/g, '+').replace(/_/g, '/')
}

const letterToNumber = {
	A: 8,
	B: 8,
	C: 0,
	D: 0,
	E: 8,
	F: 8,
	G: 0,
	H: 8,
	I: 1,
	J: 0,
	K: 8,
	L: 1,
	M: 0,
	N: 8,
	O: 0,
	P: 2,
	Q: 0,
	R: 8,
	S: 5,
	T: 7,
	U: 0,
	V: 0,
	W: 0,
	X: 0,
	Y: 1,
	Z: 0,
}

function replaceLettersWithLikelyDigits(str) {
	return str
		.split('')
		.map((digit) => {
			if (isNaN(Number(digit))) {
				console.log(digit)
				return letterToNumber[digit]
			} else return digit
		})
		.join('')
}

function App() {
	const queryString = window.location.search
	const urlParams = new URLSearchParams(queryString)
	const URLEncodedText = urlParams.get('text')
	const base64EncodedText = Base64DecodeUrl(URLEncodedText)
	// const text = atob(base64EncodedText)
	const matches = text.match(re1)
	const fixedMatches = matches
		? matches
				.map((match) => {
					if (match.length >= 12) {
						if (/\d{12}/.test(match)) {
							return match
						} else {
							return replaceLettersWithLikelyDigits(match)
						}
					} else {
						return ''
					}
				})
				.filter((match) => match)
		: []

	function navUp(i) {
		return `#upc${i - 1}`
	}
	function navDown(i) {
		return `#upc${i - 1}`
	}

	return matches ? (
		<div className='App'>
			{fixedMatches.map((match, i) => (
				<div key={i} id={`upc${i}`} className='upc'>
					<a href={navUp(i)}>up</a>
					<Barcode
						{...{
							width: 5,
							height: 500,
							format: 'CODE128',
							displayValue: true,
							fontOptions: '',
							font: 'monospace',
							textAlign: 'center',
							textPosition: 'bottom',
							textMargin: 2,
							fontSize: 40,
							background: '#ffffff',
							lineColor: '#000000',
							margin: 10,
							marginTop: undefined,
							marginBottom: undefined,
							marginLeft: undefined,
							marginRight: undefined,
						}}
						value={match}
					/>
					<a href={navDown(i)}>down</a>
				</div>
			))}
		</div>
	) : (
		<h1>no matches found</h1>
	)
}

export default App
